import Loading from "../../shared/Loading";
import Swal from "sweetalert2";
import { useContext, useEffect, useState } from "react";
import UserPost from "../../shared/UserPost";
import ContainerStyle from "../../shared/ContainerStyle";
import { getUserPosts, getTrending } from "../../../service/API";
import UserContext from "../../../contexts/UserContext";
import styled from "styled-components";
import Trending from "../../shared/Trending";


export default function MyPosts() {
    const { user, setHashList } = useContext(UserContext);
    const [posts, setPosts] = useState('');
    useEffect (() => {
        getUserPosts({ token: user.token, userId: user.user.id})
            .then((res) => setPosts(res.data))
            .catch(() => {
                Swal.fire({
                    icon: "error",
                    title: "Ops...",
                    text: "Houve uma falha ao obter os posts, por favor atualize a p치gina"
                })
            })
        getTrending(user.token)
            .then((r) => setHashList(r.data))
            .catch(() => {
                Swal.fire({
                    icon: "error",
                    title: "Ops...",
                    text: "Houve uma falha ao carregar a lista de trending, por favor atualize a p치gina"
                })
            })
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);  


    if (!posts) {
        return <Loading />
    }

    return (
        <PageContainer>
        <ContainerStyle>
        <div className="user-header">
            <h1>my posts</h1>
        </div>
        {posts.length === 0 ?
			<p style={{
				fontSize: '25px',
				color: '#ffffff',
				marginTop: '30px'}}>
			Nenhum post encontrado
			</p>:
			posts.posts.map((post) => (
            	<UserPost userInfo={post.user} post={post} key={post.id} setPosts={setPosts} posts={posts} userId={post.user.id}/>
        ))}
        </ContainerStyle>
        <Trending />
        </PageContainer>
    );
}

const PageContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;
