import { useContext, useEffect,useState } from "react"
import UserContext from "../../../contexts/UserContext"
import { getMylikes, getTrending } from "../../../service/API";
import UserPost from '../../shared/UserPost'
import styled from "styled-components";
import ContainerStyle from "../../shared/ContainerStyle";
import Trending from "../../shared/Trending";
import Swal from "sweetalert2";
import Loading from "../../shared/Loading";

export default function MyLikes() {
    const { user, setHashList } = useContext(UserContext);
    const [posts,setPosts] = useState('');

    useEffect(()=>{
        getMylikes(user.token).then((res)=> {
            setPosts(res.data)
        }).catch(Erro);

        getTrending(user.token)
            .then((r) => setHashList(r.data))
            .catch(() => {
                Swal.fire({
                    icon: "error",
                    title: "Ops...",
                    text: "Houve uma falha ao carregar a lista de trending, por favor atualize a p치gina"
                })
            })

        // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])

    if (!posts) {
        return <Loading />
    }

    function Erro(){
        Swal.fire({
            icon: "error",
            title: "Ops...",
            text: "Houve uma falha ao obter os posts, por favor atualize a p치gina"
        })
    }

    return (
        <PageContainer>
        <ContainerStyle>
        <div className="user-header">
            <h1>my likes</h1>
        </div>
        {posts.length === 0 ?
			<p style={{
				fontSize: '25px',
				color: '#ffffff',
				marginTop: '30px'}}>
			Nenhum post encontrado
			</p>:
			posts.posts.map((post) => (
            	<UserPost 
                userInfo={post.user} 
                setPosts={setPosts} 
                posts={posts}
                post={post} 
                key={post.id} 
                userId={post.user.id}/>
        ))}
        </ContainerStyle>
        <Trending />
        </PageContainer>
    )
}

const PageContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`;
