let containerMapStyles = {
    content: {
        padding: '0px',
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        transform: 'translate(-50%, -50%)',
        background: '#333333',
        borderRadius: '20px',
        width: '790px',
        minHeight: '356px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
};
let mapStyle= {
    height: '240px', 
    width: '713px',
}
let headerMapStyles = {
    fontSize: '38px',
    padding: '0 40px',
    display: 'flex', 
    justifyContent: 'space-between', 
    alignItems: 'center',
    width: '100%',
    minHeight: '81px',
    zIndex: '1', 
    backgroundColor: '#333333'
}
if (window.matchMedia("(max-width: 800px)").matches) {
    containerMapStyles = {
        content: {
            width: '100vw',
        }
    }    
}


export { 
    containerMapStyles,
    mapStyle,
    headerMapStyles 
}
